mapper.select:
SELECT p.product_code as productCode, p.eng_name as engName, s.supplier_code as supplierCode, s.supplier_name as supplierName, p.purchase_price as purchasePrice, p.weight, p.is_available as isAvailable, c.category_name as categoryName, p.source_link as sourceLink, p.created_at as createdAt, p.updated_at as updatedAt, cur.exchange_rate as exchangeRate, cur.currency_code as currencyCode FROM products p LEFT JOIN suppliers s ON p.supplier_id = s.supplier_id LEFT JOIN categories c ON p.category_id = c.category_id LEFT JOIN currencies cur ON s.currency_code = cur.currency_code WHERE 1=1
AND (p.eng_name LIKE CONCAT('%', #{searchKeyword}, '%') OR p.product_code LIKE CONCAT('%', #{searchKeyword}, '%'))
AND s.supplier_code = #{supplierCode}
ORDER BY p.created_at DESC LIMIT #{limit} OFFSET #{offset}

SELECT COUNT(*) FROM products p LEFT JOIN suppliers s ON p.supplier_id = s.supplier_id WHERE 1=1
AND (p.eng_name LIKE CONCAT('%', #{searchKeyword}, '%') OR p.product_code LIKE CONCAT('%', #{searchKeyword}, '%'))
AND s.supplier_code = #{supplierCode}
